import React from 'react'
import CookieConsent from "react-cookie-consent";

const Cookies = () => {
    return (
        <div>
            <CookieConsent
                location="bottom"
                buttonText="Sure man!!"
                cookieName="myAwesomeCookieName2"
                style={{ background: "white", color: 'black', border: 'solid', borderWidth: '1px', marginBottom: '5px', textShadow: '5px' }}
                buttonStyle={{ color: "#4e503b", fontSize: "13px", borderRadius: '10%', fontWeight: '800' }}
                expires={150}
            >
                This website uses cookies to enhance the user experience.
            </CookieConsent>
        </div>
    )
}

export default Cookies
